mapper.select:
select * from employees

where 1=1 	



	and department_id = #{department_id} 


	and job_id  = #{job_id}


	and salary  >= #{salary}



	and hire_date <= #{hire_date}


 order by first_name

insert into employees(employee_id, first_name, last_name, job_id, manager_id, department_id, hire_date, phone_number, salary, email, commission_pct) values(seq_employee.nextval,#{first_name},#{last_name}, #{job_id},
#{manager_id}
null ,
#{department_id}
null , #{hire_date}, #{phone_number}, #{salary}, #{email},#{commission_pct})

update employees set employee_id = #{employee_id}, first_name = #{first_name}, last_name = #{last_name}, job_id = #{job_id},
manager_id = #{manager_id}
manager_id = null ,
department_id = #{department_id}
department_id = null , hire_date = #{hire_date}, phone_number = #{phone_number}, salary = #{salary}, email = #{email}, commission_pct = #{commission_pct} where employee_id = #{employee_id}
